import React from 'react'
import ReactDOM from 'react-dom'

const cityOption = [
  {
    label: "New York",
    value: "new-york",
    stateabbr: "NY"
  },
  {
    label: "Los Angeles",
    value: "los-angeles",
    stateabbr: "CA"
  },
  {
    label: "Columbus",
    value: "columbus",
    stateabbr: "OH"
  }
];

export class CitySelect2 extends React.Component {

  //Initialize local state (default city value) and assign it to this.state
  constructor(props) {
    super(props); 
    this.state = {
    	city: "New York",
    	stateabbr: "NY"
    };

    this.handleSelectChange = this.handleSelectChange.bind(this);
  }

  handleSelectChange(e) {
    this.setState({ 
    	city: e.target.value, 
    	stateabbr: e.target.dataset.stateabbr 
    });
  }

  render() {
		return (
			<div className="flex justify-center content-center h-full p-0 m-0">

				<select value={this.state.city} onChange={this.handleSelectChange} className="m-auto">
					{cityOption.map((option) => (
						<option key={option.value} value={option.label} data-stateabbr={option.stateabbr}>{option.label}</option>
				  ))}
				</select>	

				<p>City: {this.state.city}</p>				
				<p>State: {this.state.stateabbr}</p>				

			</div>			
		);
	}
}

export function CitySelect(){  
	return (
		<div className="flex justify-center content-center h-full p-0 m-0">

			<select className="m-auto">
				{cityOption.map((option) => (
					<option key={option.value} value={option.label} data-stateabbr={option.stateabbr}>{option.label}</option>
			  ))}
			</select>	

			<p>City: </p>				
			<p>State: </p>				

		</div>			
	);	
}
