import React from 'react';

	function MainContent(){

		function findGeo() {

			const status = document.querySelector('#status');
			let spotifySrc;

			function success(position) {
    		const latitude  = position.coords.latitude;
    		const longitude = position.coords.longitude;
    		status.textContent = latitude + ', ' + longitude;
    	}

    	function error() {
   			status.textContent = 'Unable to retrieve your location';
  		}

  		if(!navigator.geolocation) {
   			status.textContent = 'Geolocation is not supported by your browser';
  		} else {
    		status.textContent = 'Locating…';
    		navigator.geolocation.getCurrentPosition(success, error);

    		spotifySrc = 'https://open.spotify.com/embed/album/1DFixLWuPkv3KT3TnV35m3';    		
    		document.getElementById('spotify-embed').setAttribute('src', spotifySrc);
  		}

		}

		return (
			<div className="flex justify-center content-center h-full p-0 m-0">
				<span 
					className="m-auto"					
					onClick={findGeo}

				>Get my location</span>

				<span id="status"></span>

				<iframe id="spotify-embed" src="" title="spotify-embed" width="300" height="80" frameBorder="0" allowtransparency="true" allow="encrypted-media"></iframe>

			</div>
		);
	}

export default MainContent;
