import React, { useState, useEffect } from 'react';
import { Header } from './Components/Header';
import { CitySelect } from './Components/CitySelect';
import { SpotifyEmbed } from './Components/SpotifyEmbed';

const cityOptions = {
  "new-york": {
    label: "New York",
    value: "new-york",   
    id: 7644
  },
  "los-angeles": {
    label: "Los Angeles",
    value: "los-angeles",    
    id: 17835
  },
  "columbus": {
    label: "Columbus",
    value: "columbus",   
    id: 9480
  }
};


function App() {

  const [ city, setCity ] = useState('new-york');
  const cityData = cityOptions[city];

  //Generate the Songkick API url (MOVE THIS INTO CUSTOM HOOK AND SEPARATE FILE EVENTUALLY)
  function getSongkickUrl(songkickCityId){   
    const songkickAPIKey = 'RpuYqxFiPPsJPs5l';
    let songkickUrl = "https://api.songkick.com/api/3.0/metro_areas/" + songkickCityId + "/calendar.json?apikey=" + songkickAPIKey;
    return songkickUrl;
  };

  const songkickUrl = getSongkickUrl(cityData.id);


  //Fetch list of gigs in selected city from Songkick API
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetch(songkickUrl)
      .then(res => res.json())
      .then(
        (response) => {
          setIsLoaded(true);
          setItems(response.resultsPage.results.event);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
    }, [])

  if(!isLoaded || !items.length){
    return <h1>Loading...</h1>
  }

  let randomGig = items[Math.floor(Math.random()*items.length)];
  let randomGigArtist = randomGig.performance[0].artist.displayName;
  // let randomGigVenue = items[Math.floor(Math.random()*items.length)].venue.displayName;
  // let randomGigUrl = 

  console.log(randomGigArtist);

  return (
    <div className="h-full">
      <Header />
      <p>Random Gig in {cityData.label}</p>                      

      <CitySelect
        city={city}
        setCity={setCity}
        cityOptions={cityOptions}
      />

      {/*<h2><a href=Go see <strong>{randomGigArtist}</strong> at <strong>{randomGigVenue}</strong></h2>    */}

      {/*Pass spotify ID into here*/}
      <SpotifyEmbed />
    </div>
  );
}

export default App;
