import React from 'react'

const cityOption = [
  {
    label: "New York",
    value: "new-york",
    stateAbbr: "NY"
  },
  {
    label: "Los Angeles",
    value: "los-angeles",
    stateAbbr: "CA"
  },
  {
    label: "Columbus",
    value: "columbus",
    stateAbbr: "OH"
  }
];

class CitySelect extends React.Component {

  //Initialize local state (default city value) and assign it to this.state
  constructor(props) {
    super(props); //this is required so that this.props isn't undefined
    this.state = { city: ""};

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.setState({ city: e.target.value });
    console.log(e.target.value);
  }

  render() {
		return (
			<div className="flex justify-center content-center h-full p-0 m-0">
				<select value={this.state.city} onChange={this.handleChange} className="m-auto">
					{cityOption.map((option) => (
						<option key={option.value} value={option.label} data-stateabbr={option.stateAbbr}>{option.label}</option>
				  ))}
				</select>	
				<p>{this.state.city}</p>				
			</div>			
		);
	}
}

export default CitySelect;
